// Format booking for Cal.com
const data = items[0].json;
const startTime = new Date(`${data.booking_date}T${data.booking_time}:00+02:00`);

return [{
  json: {
    eventTypeId: data.event_type_id || '{{event_type_id}}',
    start: startTime.toISOString(),
    responses: {
      name: data.customer_name || 'Client',
      email: data.email || `${data.phone}@whatsapp.botflow.co.za`,
      phone: data.phone || '',
      notes: data.notes || '',
      guests: []
    },
    timeZone: 'Africa/Johannesburg',
    language: 'en',
    metadata: {
      botId: '{{bot_id}}',
      conversationId: data.conversation_id || '',
      source: 'WhatsApp Bot'
    }
  }
}];

// Format Cal.com response
const response = items[0].json;

if (response.booking) {
  return [{
    json: {
      success: true,
      booking_id: response.booking.id,
      uid: response.booking.uid,
      start_time: response.booking.startTime,
      message: `Your appointment is confirmed!\n\nBooking ID: ${response.booking.uid}\nDate: ${new Date(response.booking.startTime).toLocaleString('en-ZA')}`
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: response.message || 'Booking failed',
      message: 'Sorry, we could not create your booking. Please try again.'
    }
  }];
}
