// Format Calendly booking link with prefill data
const user = items[0].json.resource;
const data = items[0].json;

const baseUrl = user.scheduling_url;
const eventType = data.event_type || '{{event_type_uuid}}';

// Prefill parameters
const params = new URLSearchParams();
if (data.customer_name) params.append('name', data.customer_name);
if (data.email) params.append('email', data.email);
if (data.phone) params.append('a1', data.phone); // Custom question 1
if (data.notes) params.append('a2', data.notes); // Custom question 2

const bookingUrl = `${baseUrl}/${eventType}?${params.toString()}`;

return [{
  json: {
    success: true,
    booking_url: bookingUrl,
    event_type: eventType,
    message: `Book your appointment here: ${bookingUrl}`
  }
}];
