// Format DocuSign envelope
const data = items[0].json;

return [{
  json: {
    emailSubject: data.document_subject || 'Please sign this document',
    documents: [
      {
        documentBase64: data.document_base64 || '',
        name: data.document_name || 'Document.pdf',
        fileExtension: 'pdf',
        documentId: '1'
      }
    ],
    recipients: {
      signers: [
        {
          email: data.signer_email || `${data.phone}@whatsapp.botflow.co.za`,
          name: data.signer_name || 'Client',
          recipientId: '1',
          routingOrder: '1',
          tabs: {
            signHereTabs: [
              {
                anchorString: '/s1/',
                anchorUnits: 'pixels',
                anchorXOffset: '20',
                anchorYOffset: '10'
              }
            ],
            dateSignedTabs: [
              {
                anchorString: '/d1/',
                anchorUnits: 'pixels',
                anchorXOffset: '20',
                anchorYOffset: '10'
              }
            ]
          }
        }
      ]
    },
    status: 'sent',
    customFields: {
      textCustomFields: [
        {
          name: 'botId',
          value: '{{bot_id}}'
        },
        {
          name: 'conversationId',
          value: data.conversation_id || ''
        }
      ]
    }
  }
}];

// Format DocuSign response
const response = items[0].json;

if (response.envelopeId) {
  return [{
    json: {
      success: true,
      envelope_id: response.envelopeId,
      status: response.status || 'sent',
      signing_url: response.uri || '',
      message: 'Document sent for signature! Please check your email to sign the document.'
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: response.message || 'Failed to send document',
      message: 'Sorry, we could not send the document for signature. Please try again.'
    }
  }];
}
