parameters.functionCode:
// Format event for Google Analytics 4
const data = items[0].json;

return [{
  json: {
    client_id: data.conversation_id || `wa_${data.phone}`,
    events: [
      {
        name: data.event_name || 'whatsapp_message',
        params: {
          event_category: data.event_category || 'WhatsApp',
          event_label: data.event_label || 'Message Received',
          value: data.value || 1,
          bot_id: '{{bot_id}}',
          customer_phone: data.phone || '',
          intent: data.intent || 'general',
          vertical: data.vertical || 'General',
          message_direction: data.direction || 'inbound',
          handoff_required: data.handoff_required ? 'yes' : 'no',
          session_id: data.conversation_id || '',
          engagement_time_msec: data.duration_ms || 0
        }
      }
    ]
  }
}];
