// Format gym class booking for Mindbody
const data = items[0].json;

return [{
  json: {
    siteId: data.site_id || '{{site_id}}',
    clientId: data.client_id || '',
    classId: data.class_id || '',
    classScheduleId: data.schedule_id || '',
    client: {
      firstName: data.customer_name?.split(' ')[0] || 'Member',
      lastName: data.customer_name?.split(' ').slice(1).join(' ') || 'User',
      email: data.email || `${data.phone}@whatsapp.botflow.co.za`,
      mobilePhone: data.phone || ''
    },
    waitlist: false,
    sendEmail: true,
    test: false
  }
}];

// Format Mindbody response
const response = items[0].json;

if (response.Class) {
  return [{
    json: {
      success: true,
      class_name: response.Class.ClassName || 'Class',
      instructor: response.Class.Staff?.Name || 'Instructor',
      date_time: response.Class.StartDateTime || '',
      message: `You're booked for ${response.Class.ClassName} on ${new Date(response.Class.StartDateTime).toLocaleString('en-ZA')}!`
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: response.Error?.Message || 'Booking failed',
      message: 'Sorry, we could not book your class. Please check availability and try again.'
    }
  }];
}
