// Format booking for OpenTable
const data = items[0].json;

return [{
  json: {
    restaurantId: data.restaurant_id || '{{restaurant_id}}',
    partySize: parseInt(data.party_size || 2),
    dateTime: `${data.booking_date}T${data.booking_time}:00`,
    customer: {
      firstName: data.customer_name?.split(' ')[0] || 'Guest',
      lastName: data.customer_name?.split(' ').slice(1).join(' ') || 'User',
      phone: data.phone || '',
      email: data.email || `${data.phone}@whatsapp.botflow.co.za`
    },
    specialRequests: data.dietary_requirements || data.special_requests || '',
    occasion: data.occasion || '',
    source: 'WhatsApp Bot',
    metadata: {
      botId: '{{bot_id}}',
      conversationId: data.conversation_id || ''
    }
  }
}];

// Format OpenTable response
const response = items[0].json;

if (response.confirmationNumber) {
  return [{
    json: {
      success: true,
      confirmation_number: response.confirmationNumber,
      status: response.status || 'confirmed',
      message: `Your reservation is confirmed! Confirmation number: ${response.confirmationNumber}`
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: response.error || 'Reservation failed',
      message: 'Sorry, we could not complete your reservation. Please try again or call us directly.'
    }
  }];
}
