// Extract product search query from WhatsApp message
const message = items[0].json.message || '';
const productSku = items[0].json.product_sku || '';
const productId = items[0].json.product_id || '';

return [{
  json: {
    query: message,
    sku: productSku,
    productId: productId,
    conversationId: items[0].json.conversation_id
  }
}];

// Format product data for WhatsApp response
const products = items;

if (products.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No products found matching your query.',
      products: []
    }
  }];
}

const formattedProducts = products.map(item => {
  const product = item.json;
  const variant = product.variants?.[0] || {};

  return {
    id: product.id,
    title: product.title,
    price: variant.price ? `R${variant.price}` : 'Price not available',
    inStock: variant.inventory_quantity > 0,
    quantity: variant.inventory_quantity || 0,
    sku: variant.sku || 'N/A',
    description: product.body_html?.replace(/<[^>]*>/g, '').substring(0, 200) || 'No description'
  };
});

const message = formattedProducts.map((p, i) => 
  `${i + 1}. ${p.title}\n   Price: ${p.price}\n   Stock: ${p.inStock ? `${p.quantity} available` : 'Out of stock'}\n   SKU: ${p.sku}`
).join('\n\n');

return [{
  json: {
    success: true,
    message: `Found ${products.length} product(s):\n\n${message}`,
    products: formattedProducts
  }
}];
