// Format Square response
const response = items[0].json;

if (response.payment_link) {
  return [{
    json: {
      success: true,
      payment_url: response.payment_link.url,
      payment_id: response.payment_link.id,
      amount: (response.payment_link.quick_pay.price_money.amount / 100).toFixed(2),
      message: `Please complete your payment of R${(response.payment_link.quick_pay.price_money.amount / 100).toFixed(2)} using this secure link: ${response.payment_link.url}`
    }
  }];
} else {
  return [{
    json: {
      success: false,
      error: response.errors?.[0]?.detail || 'Payment link creation failed',
      message: 'Sorry, we could not create your payment link. Please try again.'
    }
  }];
}
