// Format Wix products for WhatsApp
const response = items[0].json;
const products = response.products || [];

if (products.length === 0) {
  return [{
    json: {
      success: false,
      message: 'No products found matching your query.'
    }
  }];
}

const formattedProducts = products.map(p => ({
  id: p.id,
  name: p.name,
  price: p.price ? `R${p.price.price}` : 'Price not available',
  inStock: p.stock?.inStock || false,
  quantity: p.stock?.quantity || 0,
  sku: p.sku || 'N/A',
  description: p.description?.substring(0, 200) || ''
}));

const message = formattedProducts.map((p, i) => 
  `${i + 1}. ${p.name}\n   Price: ${p.price}\n   Stock: ${p.inStock ? `${p.quantity} available` : 'Out of stock'}`
).join('\n\n');

return [{
  json: {
    success: true,
    message: `Found ${products.length} product(s):\n\n${message}`,
    products: formattedProducts
  }
}];
